import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import {
  Box,
  Typography,
  Paper,
  TextField,
  Button,
  Stack,
  CircularProgress,
  Alert,
  Chip,
  Divider,
  IconButton,
  Tooltip,
} from '@mui/material';
import {
  Send,
  Schedule,
  Save,
  Preview,
  Refresh,
  EmojiEmotions,
  Tag,
} from '@mui/icons-material';

const ContentEditor = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [content, setContent] = useState(location.state?.idea?.content || '');
  const [scheduleTime, setScheduleTime] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  const [wordCount, setWordCount] = useState(0);

  const handleContentChange = (event) => {
    const newContent = event.target.value;
    setContent(newContent);
    setWordCount(newContent.split(/\s+/).filter(Boolean).length);
  };

  const handleSchedulePost = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch('/api/content/approve', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          content,
          scheduleTime,
        }),
      });

      if (!response.ok) {
        throw new Error('Failed to schedule post');
      }

      setSuccess(true);
      setTimeout(() => {
        navigate('/ideas');
      }, 2000);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const regenerateContent = async () => {
    setLoading(true);
    try {
      const response = await fetch('/api/content/create', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          idea: location.state?.idea,
        }),
      });

      if (!response.ok) {
        throw new Error('Failed to regenerate content');
      }

      const data = await response.json();
      setContent(data.content);
      setWordCount(data.content.split(/\s+/).filter(Boolean).length);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Box sx={{ py: 4 }}>
      <Paper elevation={3} sx={{ p: 4 }}>
        <Box sx={{ mb: 4 }}>
          <Typography variant="h4" gutterBottom>
            Content Editor
          </Typography>
          {location.state?.idea && (
            <Chip
              label={location.state.idea.title}
              color="primary"
              variant="outlined"
            />
          )}
        </Box>

        <Divider sx={{ my: 3 }} />

        <Stack spacing={3}>
          <Box>
            <Box sx={{ mb: 2, display: 'flex', justifyContent: 'space-between' }}>
              <Stack direction="row" spacing={1}>
                <Tooltip title="Add emoji">
                  <IconButton>
                    <EmojiEmotions />
                  </IconButton>
                </Tooltip>
                <Tooltip title="Add hashtag">
                  <IconButton>
                    <Tag />
                  </IconButton>
                </Tooltip>
              </Stack>
              <Typography color="text.secondary">
                Words: {wordCount}
              </Typography>
            </Box>
            <TextField
              fullWidth
              multiline
              rows={6}
              variant="outlined"
              value={content}
              onChange={handleContentChange}
              placeholder="Write your LinkedIn post here..."
            />
          </Box>

          <TextField
            type="datetime-local"
            label="Schedule Time"
            value={scheduleTime}
            onChange={(e) => setScheduleTime(e.target.value)}
            InputLabelProps={{
              shrink: true,
            }}
          />

          {error && (
            <Alert severity="error" sx={{ mt: 2 }}>
              {error}
            </Alert>
          )}

          {success && (
            <Alert severity="success" sx={{ mt: 2 }}>
              Post scheduled successfully!
            </Alert>
          )}

          <Stack direction="row" spacing={2} justifyContent="space-between">
            <Button
              variant="outlined"
              startIcon={<Refresh />}
              onClick={regenerateContent}
              disabled={loading}
            >
              Regenerate
            </Button>
            <Stack direction="row" spacing={2}>
              <Button
                variant="outlined"
                startIcon={<Preview />}
                disabled={loading}
              >
                Preview
              </Button>
              <Button
                variant="outlined"
                startIcon={<Save />}
                disabled={loading}
              >
                Save Draft
              </Button>
              <Button
                variant="contained"
                startIcon={scheduleTime ? <Schedule /> : <Send />}
                onClick={handleSchedulePost}
                disabled={loading || !content.trim()}
              >
                {loading ? (
                  <CircularProgress size={24} />
                ) : scheduleTime ? (
                  'Schedule Post'
                ) : (
                  'Post Now'
                )}
              </Button>
            </Stack>
          </Stack>
        </Stack>
      </Paper>
    </Box>
  );
};

export default ContentEditor;
