import React, { useState } from 'react';
import {
  Box,
  Paper,
  Typography,
  TextField,
  Button,
  Stepper,
  Step,
  StepLabel,
  Card,
  CardContent,
  CircularProgress,
  Alert,
  Grid,
  Chip,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from '@mui/material';
import { PostAdd, Schedule, Check } from '@mui/icons-material';

const steps = ['Generate Ideas', 'Create Content', 'Review & Schedule'];

const ContentCreator = () => {
  const [activeStep, setActiveStep] = useState(0);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [ideas, setIdeas] = useState([]);
  const [selectedIdea, setSelectedIdea] = useState(null);
  const [content, setContent] = useState('');
  const [scheduleDate, setScheduleDate] = useState('');
  const [profileUrl, setProfileUrl] = useState('');
  const [contentType, setContentType] = useState('post');
  const [tone, setTone] = useState('professional');
  const [generatedContent, setGeneratedContent] = useState(null);

  const handleGenerateIdeas = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/api/content/ideas`, {
        credentials: 'include',
      });

      if (!response.ok) {
        throw new Error('Failed to generate ideas');
      }

      const data = await response.json();
      setIdeas(data.ideas || []);
      setActiveStep(1);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateContent = async () => {
    if (!selectedIdea) return;

    setLoading(true);
    setError(null);
    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/api/content/create`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify({ idea: selectedIdea }),
      });

      if (!response.ok) {
        throw new Error('Failed to create content');
      }

      const data = await response.json();
      setContent(data.content);
      setActiveStep(2);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleSchedulePost = async () => {
    if (!content || !scheduleDate) return;

    setLoading(true);
    setError(null);
    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/api/content/approve`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify({
          content,
          scheduleTime: scheduleDate,
        }),
      });

      if (!response.ok) {
        throw new Error('Failed to schedule post');
      }

      // Reset form and show success
      setActiveStep(3);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleAnalyze = async () => {
    setLoading(true);
    setError(null);
    try {
      const token = localStorage.getItem('linkedin_token');
      const response = await fetch(`${process.env.REACT_APP_API_URL}/api/analyze-profile`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        credentials: 'include',
        body: JSON.stringify({
          profile_url: profileUrl,
          content_type: contentType,
          tone: tone,
        }),
      });

      if (!response.ok) {
        throw new Error('Failed to analyze profile');
      }

      const data = await response.json();
      setGeneratedContent(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const renderStepContent = () => {
    switch (activeStep) {
      case 0:
        return (
          <Box sx={{ textAlign: 'center', py: 4 }}>
            <Typography variant="h6" gutterBottom>
              Let's create some engaging content for your LinkedIn profile
            </Typography>
            <Button
              variant="contained"
              onClick={handleGenerateIdeas}
              startIcon={<PostAdd />}
              disabled={loading}
            >
              Generate Ideas
            </Button>
          </Box>
        );

      case 1:
        return (
          <Grid container spacing={2}>
            {ideas.map((idea, index) => (
              <Grid item xs={12} key={index}>
                <Card
                  sx={{
                    cursor: 'pointer',
                    bgcolor: selectedIdea === idea ? 'primary.light' : 'background.paper',
                  }}
                  onClick={() => setSelectedIdea(idea)}
                >
                  <CardContent>
                    <Typography variant="h6">{idea.title}</Typography>
                    <Typography color="textSecondary">{idea.description}</Typography>
                  </CardContent>
                </Card>
              </Grid>
            ))}
            <Grid item xs={12}>
              <Button
                variant="contained"
                onClick={handleCreateContent}
                disabled={!selectedIdea || loading}
                fullWidth
              >
                Create Content
              </Button>
            </Grid>
          </Grid>
        );

      case 2:
        return (
          <Box>
            <TextField
              multiline
              rows={6}
              fullWidth
              value={content}
              onChange={(e) => setContent(e.target.value)}
              label="Content"
              sx={{ mb: 2 }}
            />
            <TextField
              type="datetime-local"
              fullWidth
              value={scheduleDate}
              onChange={(e) => setScheduleDate(e.target.value)}
              label="Schedule Date"
              sx={{ mb: 2 }}
              InputLabelProps={{ shrink: true }}
            />
            <Button
              variant="contained"
              onClick={handleSchedulePost}
              startIcon={<Schedule />}
              disabled={!content || !scheduleDate || loading}
              fullWidth
            >
              Schedule Post
            </Button>
          </Box>
        );

      case 3:
        return (
          <Box sx={{ textAlign: 'center', py: 4 }}>
            <Check sx={{ fontSize: 60, color: 'success.main', mb: 2 }} />
            <Typography variant="h6" gutterBottom>
              Your content has been scheduled successfully!
            </Typography>
            <Button
              variant="contained"
              onClick={() => {
                setActiveStep(0);
                setSelectedIdea(null);
                setContent('');
                setScheduleDate('');
              }}
            >
              Create Another Post
            </Button>
          </Box>
        );

      default:
        return null;
    }
  };

  return (
    <Box sx={{ p: 3 }}>
      <Paper sx={{ p: 3, mb: 3 }}>
        <Typography variant="h5" gutterBottom>
          Generate LinkedIn Content
        </Typography>

        <TextField
          fullWidth
          label="LinkedIn Profile URL"
          value={profileUrl}
          onChange={(e) => setProfileUrl(e.target.value)}
          margin="normal"
        />

        <FormControl fullWidth margin="normal">
          <InputLabel>Content Type</InputLabel>
          <Select
            value={contentType}
            onChange={(e) => setContentType(e.target.value)}
            label="Content Type"
          >
            <MenuItem value="post">Post</MenuItem>
            <MenuItem value="article">Article</MenuItem>
            <MenuItem value="comment">Comment</MenuItem>
          </Select>
        </FormControl>

        <FormControl fullWidth margin="normal">
          <InputLabel>Tone</InputLabel>
          <Select
            value={tone}
            onChange={(e) => setTone(e.target.value)}
            label="Tone"
          >
            <MenuItem value="professional">Professional</MenuItem>
            <MenuItem value="casual">Casual</MenuItem>
            <MenuItem value="friendly">Friendly</MenuItem>
          </Select>
        </FormControl>

        {error && (
          <Alert severity="error" sx={{ mt: 2 }}>
            {error}
          </Alert>
        )}

        <Button
          variant="contained"
          onClick={handleAnalyze}
          disabled={loading || !profileUrl}
          sx={{ mt: 2 }}
        >
          {loading ? <CircularProgress size={24} /> : 'Generate Content'}
        </Button>
      </Paper>

      {generatedContent && (
        <Paper sx={{ p: 3 }}>
          <Typography variant="h6" gutterBottom>
            Generated Content
          </Typography>
          <Typography variant="body1" paragraph>
            {generatedContent.ai_content}
          </Typography>
          <Button
            variant="contained"
            color="primary"
            onClick={() => {/* Add post to LinkedIn functionality */}}
          >
            Post to LinkedIn
          </Button>
        </Paper>
      )}
    </Box>
  );
};

export default ContentCreator;
