import React, { useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import {
  Box,
  Typography,
  CircularProgress,
  Paper,
  Button,
  Alert,
} from '@mui/material';
import { LinkedIn } from '@mui/icons-material';

const LinkedInAuth = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Check if we're receiving the OAuth callback
    const urlParams = new URLSearchParams(location.search);
    const code = urlParams.get('code');
    const error = urlParams.get('error');
    const error_description = urlParams.get('error_description');

    if (error) {
      setError(decodeURIComponent(error_description) || 'Authentication failed');
    } else if (code) {
      handleAuthCallback(code);
    }
  }, [location, navigate]);

  const handleAuthCallback = async (code) => {
    setIsLoading(true);
    try {
      const response = await fetch(`/api/auth/linkedin/callback?code=${code}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Authentication failed');
      }

      const data = await response.json();
      if (data.status === 'success') {
        navigate('/profile');
      } else {
        throw new Error('Authentication failed');
      }
    } catch (err) {
      setError(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  const handleLogin = () => {
    const clientId = process.env.REACT_APP_LINKEDIN_CLIENT_ID;
    const redirectUri = 'http://localhost:5000/api/auth/linkedin/callback';  // Hardcode the exact URL

    if (!clientId) {
      setError('LinkedIn Client ID is not configured');
      return;
    }

    const params = {
      response_type: 'code',
      client_id: clientId,
      redirect_uri: redirectUri,
      scope: 'r_liteprofile r_emailaddress w_member_social',  // Use basic scopes for now
      state: crypto.randomUUID(),
    };

    const queryString = new URLSearchParams(params).toString();
    const linkedInAuthUrl = `https://www.linkedin.com/oauth/v2/authorization?${queryString}`;

    window.location.href = linkedInAuthUrl;
  };

  if (isLoading) {
    return (
      <Box
        sx={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          minHeight: '50vh',
        }}
      >
        <CircularProgress size={60} />
        <Typography variant="h6" sx={{ mt: 2 }}>
          Connecting to LinkedIn...
        </Typography>
      </Box>
    );
  }

  return (
    <Box
      sx={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        py: 4,
      }}
    >
      <Paper
        elevation={3}
        sx={{
          p: 4,
          maxWidth: 500,
          width: '100%',
          textAlign: 'center',
        }}
      >
        <LinkedIn sx={{ fontSize: 60, color: 'primary.main', mb: 2 }} />
        <Typography variant="h5" gutterBottom>
          Connect Your LinkedIn Account
        </Typography>
        <Typography color="text.secondary" paragraph>
          To get started, we need to connect to your LinkedIn profile. This allows
          us to personalize content and post on your behalf.
        </Typography>

        {error && (
          <Alert severity="error" sx={{ mb: 2 }}>
            {error}
          </Alert>
        )}

        <Button
          variant="contained"
          size="large"
          onClick={handleLogin}
          startIcon={<LinkedIn />}
          sx={{ mt: 2 }}
        >
          Connect with LinkedIn
        </Button>
      </Paper>
    </Box>
  );
};

export default LinkedInAuth;
