import React, { useState, useEffect } from 'react';
import {
  Box,
  Paper,
  Typography,
  Avatar,
  Grid,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Divider,
  Chip,
  CircularProgress,
  Alert
} from '@mui/material';
import {
  Work,
  School,
  LocationOn,
  Description,
  LinkedIn,
  Email
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';

const ProfileView = () => {
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const token = localStorage.getItem('linkedin_token');
        if (!token) {
          throw new Error('No authentication token found');
        }

        const response = await fetch(`${process.env.REACT_APP_API_URL}/api/profile`, {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Accept': 'application/json',
            'Content-Type': 'application/json',
          },
          credentials: 'include'
        });

        if (response.status === 401) {
          navigate('/auth', { 
            state: { returnTo: '/profile' }
          });
          return;
        }

        if (!response.ok) {
          const errorData = await response.json();
          throw new Error(errorData.error || 'Failed to fetch profile data');
        }

        const data = await response.json();
        setProfile(data);
      } catch (err) {
        setError(err.message);
        if (err.message === 'No authentication token found') {
          navigate('/auth', { 
            state: { returnTo: '/profile' }
          });
        }
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, [navigate]);

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', p: 4 }}>
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Box sx={{ p: 3 }}>
        <Alert severity="error">{error}</Alert>
      </Box>
    );
  }

  if (!profile) {
    return (
      <Box sx={{ p: 3 }}>
        <Alert severity="info">No profile data available</Alert>
      </Box>
    );
  }

  return (
    <Box sx={{ p: 3 }}>
      {/* Basic Info */}
      <Paper sx={{ p: 3, mb: 3 }}>
        <Grid container spacing={3}>
          <Grid item>
            <Avatar
              src={profile.profilePicture}
              sx={{ width: 120, height: 120 }}
            />
          </Grid>
          <Grid item xs>
            <Typography variant="h4">
              {profile.firstName} {profile.lastName}
            </Typography>
            <Typography variant="h6" color="text.secondary" gutterBottom>
              {profile.headline}
            </Typography>
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
              <LocationOn sx={{ mr: 1 }} color="action" />
              <Typography>{profile.location}</Typography>
            </Box>
            <Box sx={{ display: 'flex', alignItems: 'center' }}>
              <Email sx={{ mr: 1 }} color="action" />
              <Typography>{profile.email}</Typography>
            </Box>
          </Grid>
        </Grid>
      </Paper>

      {/* About */}
      {profile.about && (
        <Paper sx={{ p: 3, mb: 3 }}>
          <Typography variant="h6" gutterBottom>
            About
          </Typography>
          <Typography>{profile.about}</Typography>
        </Paper>
      )}

      {/* Experience */}
      <Paper sx={{ p: 3, mb: 3 }}>
        <Typography variant="h6" gutterBottom>
          Experience
        </Typography>
        <List>
          {profile.experience?.map((exp, index) => (
            <React.Fragment key={index}>
              <ListItem alignItems="flex-start">
                <ListItemIcon>
                  <Work />
                </ListItemIcon>
                <ListItemText
                  primary={exp.title}
                  secondary={
                    <>
                      <Typography component="span" variant="body2" color="text.primary">
                        {exp.company}
                      </Typography>
                      {` Â· ${exp.dateRange}`}
                      <br />
                      {exp.description}
                    </>
                  }
                />
              </ListItem>
              {index < profile.experience.length - 1 && <Divider />}
            </React.Fragment>
          ))}
        </List>
      </Paper>

      {/* Education */}
      {profile.education && (
        <Paper sx={{ p: 3, mb: 3 }}>
          <Typography variant="h6" gutterBottom>
            Education
          </Typography>
          <List>
            {profile.education.map((edu, index) => (
              <React.Fragment key={index}>
                <ListItem alignItems="flex-start">
                  <ListItemIcon>
                    <School />
                  </ListItemIcon>
                  <ListItemText
                    primary={edu.school}
                    secondary={
                      <>
                        <Typography component="span" variant="body2" color="text.primary">
                          {edu.degree}
                        </Typography>
                        {` Â· ${edu.dateRange}`}
                        <br />
                        {edu.description}
                      </>
                    }
                  />
                </ListItem>
                {index < profile.education.length - 1 && <Divider />}
              </React.Fragment>
            ))}
          </List>
        </Paper>
      )}

      {/* Skills */}
      {profile.skills && (
        <Paper sx={{ p: 3 }}>
          <Typography variant="h6" gutterBottom>
            Skills
          </Typography>
          <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1 }}>
            {profile.skills.map((skill, index) => (
              <Chip key={index} label={skill} />
            ))}
          </Box>
        </Paper>
      )}
    </Box>
  );
};

export default ProfileView;
