import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Typography,
  Paper,
  Avatar,
  Button,
  Grid,
  Chip,
  CircularProgress,
  Divider,
} from '@mui/material';
import {
  Person,
  Business,
  LocationOn,
  ArrowForward,
} from '@mui/icons-material';

const ProfileView = () => {
  const navigate = useNavigate();
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchProfile();
  }, []);

  const fetchProfile = async () => {
    try {
      const token = localStorage.getItem('linkedin_token');
      if (!token) {
        throw new Error('No authentication token found');
      }

      console.log('Using token:', token); // Debug log

      const response = await fetch(`${process.env.REACT_APP_API_URL}/api/profile`, {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Accept': 'application/json',
          'Content-Type': 'application/json',
        },
        credentials: 'include'
      });

      console.log('Response status:', response.status); // Debug log

      if (response.status === 401) {
        // Token expired or invalid
        localStorage.removeItem('linkedin_token'); // Clear invalid token
        navigate('/auth', { 
          state: { returnTo: '/profile' }
        });
        return;
      }

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to fetch profile data');
      }

      const data = await response.json();
      setProfile(data);
    } catch (err) {
      console.error('Profile fetch error:', err); // Debug log
      setError(err.message);
      if (err.message === 'No authentication token found') {
        navigate('/auth', { 
          state: { returnTo: '/profile' }
        });
      }
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', py: 4 }}>
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Box sx={{ textAlign: 'center', py: 4 }}>
        <Typography color="error" gutterBottom>
          {error}
        </Typography>
        <Button
          variant="contained"
          onClick={() => navigate('/auth')}
          sx={{ mt: 2 }}
        >
          Reconnect to LinkedIn
        </Button>
      </Box>
    );
  }

  return (
    <Box sx={{ py: 4 }}>
      <Paper elevation={3} sx={{ p: 4, mb: 4 }}>
        <Grid container spacing={4}>
          <Grid item xs={12} md={4} sx={{ textAlign: 'center' }}>
            <Avatar
              sx={{
                width: 120,
                height: 120,
                mx: 'auto',
                mb: 2,
                bgcolor: 'primary.main',
              }}
            >
              <Person sx={{ fontSize: 60 }} />
            </Avatar>
            <Typography variant="h5" gutterBottom>
              {profile?.firstName} {profile?.lastName}
            </Typography>
            <Chip
              icon={<Business />}
              label={profile?.industry}
              sx={{ mb: 1 }}
            />
            <Chip
              icon={<LocationOn />}
              label={profile?.location}
              sx={{ ml: 1 }}
            />
          </Grid>

          <Grid item xs={12} md={8}>
            <Typography variant="h6" gutterBottom>
              {profile?.headline}
            </Typography>
            <Divider sx={{ my: 2 }} />
            <Typography color="text.secondary" paragraph>
              {profile?.summary}
            </Typography>
          </Grid>
        </Grid>
      </Paper>

      <Box sx={{ textAlign: 'center' }}>
        <Typography variant="h6" gutterBottom>
          Ready to create some engaging content?
        </Typography>
        <Button
          variant="contained"
          size="large"
          onClick={() => navigate('/ideas')}
          endIcon={<ArrowForward />}
          sx={{ mt: 2 }}
        >
          Generate Content Ideas
        </Button>
      </Box>
    </Box>
  );
};

export default ProfileView;
