import React from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Typography,
  Button,
  Paper,
  Stack,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
} from '@mui/material';
import {
  AutoAwesome,
  AccountCircle,
  Lightbulb,
  Edit,
  Schedule,
} from '@mui/icons-material';

const Welcome = () => {
  const navigate = useNavigate();

  const features = [
    {
      icon: <AccountCircle color="primary" />,
      title: 'Profile Integration',
      description: 'Connect your LinkedIn profile to personalize content',
    },
    {
      icon: <Lightbulb color="primary" />,
      title: 'AI-Powered Ideas',
      description: 'Get smart content suggestions based on your expertise',
    },
    {
      icon: <Edit color="primary" />,
      title: 'Content Creation',
      description: 'Create engaging posts with AI assistance',
    },
    {
      icon: <Schedule color="primary" />,
      title: 'Smart Scheduling',
      description: 'Schedule posts for optimal engagement times',
    },
  ];

  return (
    <Box sx={{ textAlign: 'center', py: 4 }}>
      <Typography
        variant="h2"
        component="h1"
        gutterBottom
        sx={{
          fontWeight: 'bold',
          color: 'primary.main',
          mb: 4,
        }}
      >
        <AutoAwesome sx={{ mr: 2, fontSize: 45 }} />
        LinkedIn Content Creator
      </Typography>

      <Typography variant="h5" color="text.secondary" paragraph>
        Create engaging LinkedIn content that resonates with your professional network
      </Typography>

      <Paper elevation={3} sx={{ p: 4, my: 4, maxWidth: 800, mx: 'auto' }}>
        <Typography variant="h6" gutterBottom>
          Key Features
        </Typography>
        <List>
          {features.map((feature, index) => (
            <ListItem key={index}>
              <ListItemIcon>{feature.icon}</ListItemIcon>
              <ListItemText
                primary={feature.title}
                secondary={feature.description}
              />
            </ListItem>
          ))}
        </List>
      </Paper>

      <Stack
        direction="row"
        spacing={2}
        justifyContent="center"
        sx={{ mt: 4 }}
      >
        <Button
          variant="contained"
          size="large"
          onClick={() => navigate('/auth')}
          startIcon={<AccountCircle />}
          sx={{ py: 2, px: 4 }}
        >
          Get Started with LinkedIn
        </Button>
      </Stack>
    </Box>
  );
};

export default Welcome;
