import React, { useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import {
  Box,
  Typography,
  CircularProgress,
  Paper,
  Button,
  Alert,
} from '@mui/material';
import { LinkedIn } from '@mui/icons-material';

const LinkedInAuth = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const code = urlParams.get('code');
    const state = urlParams.get('state');
    const error = urlParams.get('error');
    const error_description = urlParams.get('error_description');

    if (error || error_description) {
      setError(error_description || error || 'Authentication failed');
    } else if (code) {
      handleAuthCallback(code, state);
    }
  }, []);

  useEffect(() => {
    handleAuthCallback();
  }, [handleAuthCallback]);

  const handleAuthCallback = async (code, state) => {
    setIsLoading(true);
    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/api/auth/linkedin/callback?code=${code}&state=${state}`, {
        method: 'GET',
        headers: {
          'Accept': 'application/json',
        },
        credentials: 'include',
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Authentication failed');
      }

      // Store user data
      localStorage.setItem('linkedin_token', data.access_token);
      localStorage.setItem('user', JSON.stringify(data.user));
      localStorage.setItem('profile', JSON.stringify(data.profile));

      // Redirect to profile page or stored return path
      const returnTo = localStorage.getItem('returnTo') || '/profile';
      navigate(returnTo);
      localStorage.removeItem('returnTo'); // Clean up
    } catch (err) {
      console.error('Auth error:', err);
      setError(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  const handleLogin = async () => {
    try {
      // Get the authorization URL from our backend
      const response = await fetch(`${process.env.REACT_APP_API_URL}/api/auth/linkedin/`, {
        credentials: 'include'
      });
      const data = await response.json();

      // Store return path
      localStorage.setItem('returnTo', location.state?.returnTo || '/profile');

      // Redirect to LinkedIn's authorization page
      window.location.href = data.auth_url;
    } catch (err) {
      setError('Failed to start authentication process');
    }
  };

  // If we're on the callback route, show loading
  if (location.pathname === '/auth/callback') {
    return (
      <Box
        sx={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          minHeight: '100vh',
        }}
      >
        <CircularProgress size={60} />
        <Typography variant="h6" sx={{ mt: 2 }}>
          Connecting to LinkedIn...
        </Typography>
      </Box>
    );
  }

  // Show loading spinner while processing
  if (isLoading) {
    return (
      <Box
        sx={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          minHeight: '50vh',
        }}
      >
        <CircularProgress size={60} />
        <Typography variant="h6" sx={{ mt: 2 }}>
          Connecting to LinkedIn...
        </Typography>
      </Box>
    );
  }

  // Show the login button
  return (
    <Box
      sx={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        py: 4,
      }}
    >
      <Paper
        elevation={3}
        sx={{
          p: 4,
          maxWidth: 500,
          width: '100%',
          textAlign: 'center',
        }}
      >
        <LinkedIn sx={{ fontSize: 60, color: 'primary.main', mb: 2 }} />
        <Typography variant="h5" gutterBottom>
          Connect Your LinkedIn Account
        </Typography>
        <Typography color="text.secondary" paragraph>
          To get started, we need to connect to your LinkedIn profile. This allows
          us to personalize content and post on your behalf.
        </Typography>

        {error && (
          <Alert severity="error" sx={{ mb: 2 }}>
            {error}
          </Alert>
        )}

        <Button
          variant="contained"
          size="large"
          onClick={handleLogin}
          startIcon={<LinkedIn />}
          sx={{ mt: 2 }}
        >
          Connect with LinkedIn
        </Button>
      </Paper>
    </Box>
  );
};

export default LinkedInAuth;
