import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Typography,
  Paper,
  Card,
  CardContent,
  CardActions,
  Button,
  Grid,
  CircularProgress,
  Chip,
} from '@mui/material';
import {
  Lightbulb,
  Edit,
  Refresh,
  TrendingUp,
} from '@mui/icons-material';

const ContentIdeas = () => {
  const navigate = useNavigate();
  const [ideas, setIdeas] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchIdeas();
  }, []);

  const fetchIdeas = async () => {
    setLoading(true);
    try {
      const response = await fetch('/api/content/ideas');
      if (!response.ok) {
        throw new Error('Failed to fetch content ideas');
      }
      const data = await response.json();
      setIdeas(data.ideas || []);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateContent = (idea) => {
    navigate('/editor', { state: { idea } });
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', py: 8 }}>
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Box sx={{ textAlign: 'center', py: 4 }}>
        <Typography color="error" gutterBottom>
          {error}
        </Typography>
        <Button
          variant="contained"
          onClick={fetchIdeas}
          startIcon={<Refresh />}
          sx={{ mt: 2 }}
        >
          Try Again
        </Button>
      </Box>
    );
  }

  return (
    <Box sx={{ py: 4 }}>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 4 }}>
        <Typography variant="h4" gutterBottom>
          <Lightbulb sx={{ mr: 1, verticalAlign: 'bottom' }} />
          Content Ideas
        </Typography>
        <Button
          variant="outlined"
          startIcon={<Refresh />}
          onClick={fetchIdeas}
        >
          Refresh Ideas
        </Button>
      </Box>

      <Grid container spacing={3}>
        {ideas.map((idea, index) => (
          <Grid item xs={12} md={6} key={index}>
            <Card elevation={2}>
              <CardContent>
                <Typography variant="h6" gutterBottom>
                  {idea.title}
                </Typography>
                <Typography color="text.secondary" paragraph>
                  {idea.description}
                </Typography>
                <Box sx={{ mb: 2 }}>
                  <Chip
                    icon={<TrendingUp />}
                    label="Trending Topic"
                    size="small"
                    color="secondary"
                    sx={{ mr: 1 }}
                  />
                </Box>
              </CardContent>
              <CardActions>
                <Button
                  variant="contained"
                  startIcon={<Edit />}
                  onClick={() => handleCreateContent(idea)}
                  fullWidth
                >
                  Create Content
                </Button>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>

      {ideas.length === 0 && !loading && (
        <Paper sx={{ p: 4, textAlign: 'center' }}>
          <Typography variant="h6" gutterBottom>
            No content ideas available
          </Typography>
          <Button
            variant="contained"
            onClick={fetchIdeas}
            startIcon={<Refresh />}
            sx={{ mt: 2 }}
          >
            Generate New Ideas
          </Button>
        </Paper>
      )}
    </Box>
  );
};

export default ContentIdeas;
