import React from 'react';
import { 
  Box, 
  Container, 
  AppBar, 
  Toolbar, 
  Typography,
  Drawer,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  IconButton,
  Divider
} from '@mui/material';
import { useLocation, useNavigate } from 'react-router-dom';
import {
  Menu as MenuIcon,
  Analytics as AnalyticsIcon,
  Home as HomeIcon,
  Person as PersonIcon,
  Add as AddIcon,
  History as HistoryIcon
} from '@mui/icons-material';

const Layout = ({ children }) => {
  const location = useLocation();
  const navigate = useNavigate();
  const [drawerOpen, setDrawerOpen] = React.useState(false);

  const menuItems = [
    {
      text: 'Home',
      icon: <HomeIcon />,
      path: '/',
    },
    {
      text: 'Profile Analyzer',
      icon: <AnalyticsIcon />,
      path: '/analyze',
    },
    {
      text: 'Profile',
      icon: <PersonIcon />,
      path: '/profile',
      requiresAuth: true,
    },
    {
      text: 'Create Content',
      icon: <AddIcon />,
      path: '/content/create',
      requiresAuth: true,
    },
    {
      text: 'Content History',
      icon: <HistoryIcon />,
      path: '/content/history',
      requiresAuth: true,
    },
  ];

  const getPageTitle = () => {
    switch (location.pathname) {
      case '/':
        return 'Welcome';
      case '/auth':
        return 'LinkedIn Authentication';
      case '/profile':
        return 'Your Profile';
      case '/analyze':
        return 'Profile Analyzer';
      case '/content/create':
        return 'Create Content';
      case '/content/history':
        return 'Content History';
      default:
        return 'LinkedIn Content Creator';
    }
  };

  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
      <AppBar position="static">
        <Toolbar>
          <IconButton
            color="inherit"
            edge="start"
            onClick={() => setDrawerOpen(true)}
            sx={{ mr: 2 }}
          >
            <MenuIcon />
          </IconButton>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            {getPageTitle()}
          </Typography>
        </Toolbar>
      </AppBar>

      <Drawer
        anchor="left"
        open={drawerOpen}
        onClose={() => setDrawerOpen(false)}
      >
        <List sx={{ width: 250 }}>
          {menuItems.map((item) => (
            <ListItem
              button
              key={item.text}
              onClick={() => {
                navigate(item.path);
                setDrawerOpen(false);
              }}
              sx={{
                bgcolor: location.pathname === item.path ? 'action.selected' : 'inherit'
              }}
            >
              <ListItemIcon>{item.icon}</ListItemIcon>
              <ListItemText primary={item.text} />
            </ListItem>
          ))}
        </List>
      </Drawer>

      <Container component="main" sx={{ 
        mt: 4, 
        mb: 4,
        flex: 1,
        display: 'flex',
        flexDirection: 'column'
      }}>
        {children}
      </Container>

      <Box component="footer" sx={{ 
        py: 3, 
        px: 2, 
        mt: 'auto',
        backgroundColor: (theme) => theme.palette.grey[200]
      }}>
        <Container maxWidth="sm">
          <Typography variant="body2" color="text.secondary" align="center">
            © {new Date().getFullYear()} LinkedIn Content Creator. All rights reserved.
          </Typography>
        </Container>
      </Box>
    </Box>
  );
};

export default Layout;
