import React, { useState } from 'react';
import {
  Box,
  Button,
  TextField,
  Typography,
  Card,
  CardContent,
  CircularProgress,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Alert,
  Tabs,
  Tab,
} from '@mui/material';
import { Analytics as AnalyticsIcon } from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';

const ProfileAnalyzer = () => {
  const [profileUrl, setProfileUrl] = useState('');
  const [contentType, setContentType] = useState('post');
  const [tone, setTone] = useState('professional');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [result, setResult] = useState(null);
  const [activeTab, setActiveTab] = useState(0);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      // Check if user is authenticated
      const token = localStorage.getItem('linkedin_token');
      if (!token) {
        // Redirect to auth if not authenticated
        navigate('/auth', { 
          state: { 
            returnTo: '/analyze',
            profileUrl: profileUrl 
          } 
        });
        return;
      }

      const response = await fetch(`${process.env.REACT_APP_API_URL}/api/analyze-profile`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        credentials: 'include',
        body: JSON.stringify({
          profile_url: profileUrl,
          content_type: contentType,
          tone: tone,
        }),
      });

      if (response.status === 401) {
        // Token expired or invalid, redirect to auth
        navigate('/auth', { 
          state: { 
            returnTo: '/analyze',
            profileUrl: profileUrl 
          } 
        });
        return;
      }

      if (!response.ok) {
        throw new Error('Failed to analyze profile');
      }

      const data = await response.json();
      setResult(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleTabChange = (event, newValue) => {
    setActiveTab(newValue);
  };

  return (
    <Box sx={{ maxWidth: 800, mx: 'auto', p: 3 }}>
      <Typography variant="h4" gutterBottom>
        LinkedIn Profile Analyzer
      </Typography>

      <Card sx={{ mb: 3 }}>
        <CardContent>
          <form onSubmit={handleSubmit}>
            <TextField
              fullWidth
              label="LinkedIn Profile URL"
              value={profileUrl}
              onChange={(e) => setProfileUrl(e.target.value)}
              margin="normal"
              required
              placeholder="https://www.linkedin.com/in/username"
            />

            <Box sx={{ display: 'flex', gap: 2, my: 2 }}>
              <FormControl fullWidth>
                <InputLabel>Content Type</InputLabel>
                <Select
                  value={contentType}
                  onChange={(e) => setContentType(e.target.value)}
                  label="Content Type"
                >
                  <MenuItem value="post">Post</MenuItem>
                  <MenuItem value="article">Article</MenuItem>
                  <MenuItem value="comment">Comment</MenuItem>
                </Select>
              </FormControl>

              <FormControl fullWidth>
                <InputLabel>Tone</InputLabel>
                <Select
                  value={tone}
                  onChange={(e) => setTone(e.target.value)}
                  label="Tone"
                >
                  <MenuItem value="professional">Professional</MenuItem>
                  <MenuItem value="casual">Casual</MenuItem>
                  <MenuItem value="motivational">Motivational</MenuItem>
                  <MenuItem value="educational">Educational</MenuItem>
                </Select>
              </FormControl>
            </Box>

            <Button
              type="submit"
              variant="contained"
              fullWidth
              disabled={loading}
              startIcon={loading ? <CircularProgress size={20} /> : <AnalyticsIcon />}
            >
              {loading ? 'Analyzing...' : 'Analyze Profile'}
            </Button>
          </form>
        </CardContent>
      </Card>

      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}

      {result && (
        <Card>
          <CardContent>
            <Tabs value={activeTab} onChange={handleTabChange} sx={{ mb: 2 }}>
              <Tab label="Profile Analysis" />
              <Tab label="Generated Content" />
            </Tabs>

            {activeTab === 0 && (
              <Box>
                <Typography variant="h6" gutterBottom>
                  Profile Information
                </Typography>
                <Typography><strong>Name:</strong> {result.profile_analysis.name}</Typography>
                <Typography><strong>Headline:</strong> {result.profile_analysis.headline}</Typography>
                <Typography><strong>About:</strong> {result.profile_analysis.about}</Typography>

                <Typography variant="h6" sx={{ mt: 2 }}>Experience</Typography>
                {result.profile_analysis.experience.map((exp, index) => (
                  <Typography key={index}>
                    • {exp.title} at {exp.company}
                  </Typography>
                ))}

                <Typography variant="h6" sx={{ mt: 2 }}>Skills</Typography>
                <Typography>
                  {result.profile_analysis.skills.join(', ')}
                </Typography>
              </Box>
            )}

            {activeTab === 1 && (
              <Box>
                <Typography variant="h6" gutterBottom>
                  AI-Generated Content
                </Typography>
                <Typography sx={{ whiteSpace: 'pre-wrap', mb: 3 }}>
                  {result.ai_content}
                </Typography>

                <Typography variant="h6" gutterBottom>
                  Template-Based Content
                </Typography>
                <Typography sx={{ whiteSpace: 'pre-wrap' }}>
                  {result.template_content}
                </Typography>
              </Box>
            )}
          </CardContent>
        </Card>
      )}
    </Box>
  );
};

export default ProfileAnalyzer;
